package pickr.classes;


import java.util.ArrayList;
//import java.util.Collections;
import java.util.Collections;

import pickr.enums.Suit;
import pickr.exceptions.OutOfBoundException;
import pickr.exceptions.WrongCardNumberException;

public class Deck {
	
	public ArrayList<GamingCard> cards;
	
	
	public Deck() { this.cards = new ArrayList<GamingCard>();	}

	public ArrayList<GamingCard> getCards() { return cards;	}
	
	public void init() throws WrongCardNumberException {
		for(int num = 1; num < 11; num++) {

			for(Suit suit : Suit.values()) {
				cards.add(new GamingCard(suit, num));			
			}
		}
	}
	

	public void shuffle() { Collections.shuffle(cards);	}
	
	public GamingCard pick() throws OutOfBoundException {
	
		if(!cards.isEmpty()) 
			return cards.remove(0);
		else throw new OutOfBoundException();
	
	}
	

	public ArrayList<GamingCard> picks(int n) throws OutOfBoundException {	
		
		if(cards.size() >= n) {
			ArrayList<GamingCard> picked = new ArrayList<GamingCard>();
			for(int i =0; i < n; i ++) {
				picked.add(cards.remove(0));
			}
			return picked;			
		}
		else throw new OutOfBoundException();	
	}	
}

package pickr.classes;

import pickr.enums.Suit;
import pickr.exceptions.WrongCardNumberException;

public class GamingCard {
	
	private Suit suit;
	private int num;
	private int val;
	
	public GamingCard(Suit suit, int num) throws WrongCardNumberException {
	
		this.suit = suit;
		this.val = numToVal(num);
		this.num = checkNum(num);
	}
	 
	public Suit getSuit() { return this.suit; }
	
	public int getNum() { return this.num; }
	
	public int getVal() { return this.val; }
	
	
	int numToVal(int num) {
		if(num != 1 && num != 3) return num-1;
		else return num == 1 ? 10 : 9;		
	}
	
	int checkNum(int num) throws WrongCardNumberException {
	 
		    if (num < 1 || num > 10) {
		      throw new WrongCardNumberException();
		    }
		    return num;
	 }
	
	@Override
	public String toString() {	return num + " " + suit.toString() + " value: " + val;	}

}

package pickr.classes;


import java.util.ArrayList;

public class Player {
	
	private ArrayList<GamingCard> cards;
	
	private String id;
	
	private int gameScore;
	
	private int matchScore;
	
	public Player(String id) {
		this.id = id;
		this.cards = new ArrayList<GamingCard>();
		this.gameScore = 0;
		this.matchScore = 0;
	}

	
	public ArrayList<GamingCard> getCards() { return cards;	}

	public void hand(ArrayList<GamingCard> cards) { this.cards = cards;	}
	
	public GamingCard drop() { return this.cards.get(0); }

	public int getGameScore() {	return gameScore; }

	public void setGameScore(int gameScore) { this.gameScore += gameScore;	}

	public int getMatchScore() { return matchScore; }

	public void setMatchScore(int matchScore) {	this.matchScore = matchScore; }

	public String getId() {	return id;	}

	public void delete() { this.cards.remove(0); }
	
	public void pick(GamingCard card) { this.cards.add(card);  }

}

