package pickr.games;

import pickr.classes.GamingCard;
import pickr.classes.Player;
import pickr.enums.Suit;
import pickr.exceptions.OutOfBoundException;
import pickr.exceptions.WrongCardNumberException;

public class Briscola extends Game{
	
	private int numcards = 3;
	
	private Suit briscola;
	
	public Briscola(int numplayers, int score) {
		super(numplayers, score);
	}
	

	@Override
	void spread() throws OutOfBoundException {
		for(Player player : players) {
			player.hand(deck.picks(numcards));
		}		
	}

	@Override
	public void start() throws WrongCardNumberException, OutOfBoundException { 	
		
		this.deck.init();
		// this.deck.shuffle();
		
		briscola = deck.getCards().get(deck.getCards().size()-1).getSuit();
		
		this.spread();
		
		// this.deck.show();
	}


	@Override
	GamingCard rule() {
		
		GamingCard winningCard = this.getTable().get(0);
		
		// assert(this.getTable().size() == this.numplayers);
		for(GamingCard card : this.getTable()) {			
			
			if(card.getVal() > winningCard.getVal()) {
				// T T T
				if(winningCard.getSuit() == briscola && card.getSuit() == briscola) {
					winningCard = card;
				}
				// T F F 
				if(winningCard.getSuit() != briscola && card.getSuit() != briscola) {
					winningCard = card;
				}
			}
			// F T F 
			else if(card.getSuit() == briscola && winningCard.getSuit() != briscola) {
				winningCard = card;
			}			
		}
		
		System.out.println("Briscola.rule() : " + winningCard);
				
		return winningCard;
	}
	



	public void round() throws OutOfBoundException {
		
		System.out.println("Briscola.round() : " + briscola.toString());
		
		for(Player player : this.players) {
			this.dropCard(player.drop());
		}
		
		System.out.println("Briscola.round() : " + this.getTable());
		
		GamingCard winningCard = this.rule();
		
		this.winner(winningCard);	
		
		for(Player player : this.players) {
			player.pick(this.deck.pick());
		}		
	}
}

package pickr.games;


import java.util.ArrayList;

import pickr.classes.Deck;
import pickr.classes.GamingCard;
import pickr.classes.Player;
import pickr.exceptions.OutOfBoundException;
import pickr.exceptions.WrongCardNumberException;

public abstract class Game {
	
	int numplayers;
	int score;
	private int round = 0;
	protected Deck deck;
	private ArrayList<GamingCard> table;
	protected ArrayList<Player> players;
	
	public Game(int numplayers, int score) {
		this.numplayers = numplayers;
		this.score = score;
		this.deck = new Deck();
		this.table = new ArrayList<GamingCard>();
		this.players = new ArrayList<Player>();
	}

	public Deck getDeck() { return deck; }

	public int getRound() { return round; }

	public ArrayList<GamingCard> getTable() { return table; }

	public ArrayList<Player> getPlayers() { return players; }
	
	abstract void spread() throws OutOfBoundException;
	
	abstract GamingCard rule();
	
	public void addPlayer(String id) throws OutOfBoundException {
		if(players.size() < this.numplayers) {
			players.add(new Player(id));
		}
		else throw new OutOfBoundException();
	}
	
	int getTableValue() {
		int result = 0;
		for(GamingCard card : this.table) {
			result += card.getVal();
		}
		return result;
	}
	
	void dropCard(GamingCard card) { table.add(card); }
		
	abstract void start() throws WrongCardNumberException, OutOfBoundException;	
	
	public Player winner(GamingCard winnerCard) {		
		
		Player winner = this.players.get(0);
		
		for(Player player : this.players) {
				
			if(player.getCards().contains(winnerCard)) winner = player;
			
			player.delete();
		}
		
		System.out.println("the winner is " + winner.getId());	
		
		winner.setGameScore(getTableValue());
		
		table.clear();
		
		this.round += 1;
		
		return winner;		
	}

}
